import axios from 'axios';

const Index = ({dados}) => (
    <div>
        <h1>Palestrantes</h1>
        {/* {console.log(dados)} */}
        <ul>
            {response.map(atividades => (
                <li key={atividades.id}>
                    <h2>{atividades.nome}</h2>
                    <p>{atividades.tipo}</p>
                </li>
            ))}
        </ul>
    </div>
);

Index.getInitialProps = async () =>{
    const response = await axios.get(
        'https://api.padraoeditorial.com.br/conarec/api/atividades'
    );
        // console.log(response);
    return { dados: response.atividades }
};

export default Index;
